mapper.update:
update disease
set del_flag = 1
where id = #{id,jdbcType=INTEGER}

mapper.delete:
delete from product_disease
where product_code = #{pCode}
and disease_id = #{did}

mapper.insert:
insert into disease (id, name, pid, 
  sort, create_no, update_no, 
  del_flag, create_time, update_time
  )
values (#{id,jdbcType=INTEGER}, #{name,jdbcType=VARCHAR}, #{pid,jdbcType=INTEGER}, 
  #{sort,jdbcType=INTEGER}, #{createNo,jdbcType=VARCHAR}, #{updateNo,jdbcType=VARCHAR}, 
  #{delFlag,jdbcType=BIT}, #{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}
  )
