AND (`name` LIKE CONCAT(CONCAT('%',#{keyword,jdbcType=VARCHAR}),'%') OR product_code LIKE CONCAT(CONCAT('%',#{keyword,jdbcType=VARCHAR}),'%'))
AND category_dict_code = #{categoryDictCode}
AND category_dict_code = #{categoryDictCode}
AND disease_id = #{diseaseId}
AND brand_no = #{brandNo}
AND status = #{status}
AND sale_price BETWEEN #{downPrice} AND #{upPrice}
order by create_time desc

